import { __decorate } from "tslib";
import { Component } from '@angular/core';
let ShowDepComponent = class ShowDepComponent {
    constructor(service, modalService) {
        this.service = service;
        this.modalService = modalService;
        this.DepartmentList = [];
        this.ModalTitle = "";
        this.ActivateAddEditDepComp = false;
        this.DepartmentIdFilter = "";
        this.DepartmentNameFilter = "";
        this.DepartmentListWithoutFilter = [];
    } //<--- initializing shared service
    ngOnInit() {
        this.refreshDepList();
    }
    refreshDepList() {
        this.service.getDepList().subscribe(data => {
            //call is received and only then assign value to the list variable - asynchronous operation
            this.DepartmentList = data;
        });
    }
    addClick() {
        this.dep = {
            DepartmentId: 0,
            DepartmentName: ""
        };
        this.ModalTitle = "Add Department";
        this.ActivateAddEditDepComp = true;
    }
    closeClick() {
        this.ActivateAddEditDepComp = false;
        this.refreshDepList();
    }
    deleteClick(item) {
        if (confirm("Are you sure you want to delete this department?")) {
            this.service.deleteDepartment(item.DepartmentId).subscribe(data => {
                alert(data.toString());
            });
        }
        this.ActivateAddEditDepComp = true;
    }
};
ShowDepComponent = __decorate([
    Component({
        selector: 'app-show-dep',
        templateUrl: './show-dep.component.html',
        styleUrls: ['./show-dep.component.css']
    })
], ShowDepComponent);
export { ShowDepComponent };
