import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { ModalDismissReasons } from '@ng-bootstrap/ng-bootstrap';
let ShowDepComponent = class ShowDepComponent {
    constructor(service, modalService) {
        this.service = service;
        this.modalService = modalService;
        this.DepartmentList = [];
        this.ModalTitle = "";
        this.ActivateAddEditDepComp = false;
        this.DepartmentIdFilter = "";
        this.DepartmentNameFilter = "";
        this.DepartmentListWithoutFilter = [];
        this.closeResult = '';
    } //<--- initializing shared service
    ngOnInit() {
        this.refreshDepList();
    }
    refreshDepList() {
        this.service.getDepList().subscribe(data => {
            //call is received and only then assign value to the list variable - asynchronous operation
            this.DepartmentList = data;
        });
    }
    addClick() {
        this.dep = {
            DepartmentId: 0,
            DepartmentName: ""
        };
        this.ModalTitle = "Add Department";
        this.ActivateAddEditDepComp = true;
    }
    closeClick() {
        this.ActivateAddEditDepComp = false;
        this.refreshDepList();
    }
    open(content) {
        this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title' }).result.then((result) => {
            this.closeResult = `Closed with: ${result}`;
        }, (reason) => {
            this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
        });
    }
    getDismissReason(reason) {
        if (reason === ModalDismissReasons.ESC) {
            return 'by pressing ESC';
        }
        else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
            return 'by clicking on a backdrop';
        }
        else {
            return `with: ${reason}`;
        }
    }
};
ShowDepComponent = __decorate([
    Component({
        selector: 'app-show-dep',
        templateUrl: './show-dep.component.html',
        styleUrls: ['./show-dep.component.css']
    })
], ShowDepComponent);
export { ShowDepComponent };
